function chunkArrayInGroups(arr, size) {
  var count=0;
  let j=0
  var a=[]
  var temp=[]


  //console.log(arr[1])

for(let i=0;i<arr.length;i++)
{ 
  while(count<size)
  {
    if(i<arr.length)
    {
    temp.push(arr[i]);
    count++;
    i++;
    }
  }
  a.push(temp)
  i--;
  count=0;
  j++;
  temp=[]
}
  return a;
}

console.log(chunkArrayInGroups(["a", "b", "c", "d"], 2));
