function uniteUnique(arr,...a) {
  for(let i=0;i<a.length;i++)
  {
    for(let j=0;j<a[i].length;j++)
    {
      if(arr.includes(a[i][j]))
      {
        continue;
      }
      else
      {
        arr.push(a[i][j])
      }
    }
  }

  return arr;
}

